class MyAction extends Action {
  openUrl(url) {
    // 使用给定的URL或默认打开京东App
    const params = `{"category":"jump","des":"m","url":"${url}","imkParam":{"os":"ios","device":"iphone"}}`;
    const openURL = `openApp.jdMobile://virtual?params=${encodeURI(params)}`;
    $app.openURL(openURL);
  }

  do() {
    const regex =
      /(https?:\/\/)([\da-z\.-]+)\.([a-z\.]{2,6})([:0-9])*([\/\w\#\.\-\?\=\&])*\s?/gi;
    const text = this.text ?? ($clipboard.text || "");
    const url = text.match(regex, text) ?? [];
    if (url.length > 1) {
      $ui.menu({
        items: url,
        handler: (title, index) => {
          this.openUrl(url[index]);
        },
      });
    } else if (url.length === 1) {
      this.openUrl(url[0]);
    } else {
      $ui.warning("未检测到链接,将打开京东首页");
      setTimeout(() => {
        this.openUrl(""); 
      }, 2000); 
    }
  }
}
