class MyCodecAction {
    constructor() {
        this.text = $clipboard.text;
        this.mappingChars = "XKNWSPRMCTGZVDHF"; // BinaryLetter默认映射字符串，可自定义修改加密 如:ABCDEFGHJKLMNPQR

        this.charMap = {};
        this.mappingChars.split('').forEach((char, index) => {
            const binary = index.toString(2).padStart(4, '0');
            this.charMap[char] = binary;
        });

        this.reverseCharMap = Object.fromEntries(
            Object.entries(this.charMap).map(([k, v]) => [v, k])
        );
    }

    binaryLetterDecode(str) {
        let binaryString = "";
        for (let i = 0; i < str.length; i++) {
            if (!this.charMap[str[i]]) {
                throw new Error("BinaryLetter解码失败: 输入包含未知字符");
            }
            binaryString += this.charMap[str[i]];
        }
        let result = "";
        for (let i = 0; i < binaryString.length; i += 8) {
            let byte = binaryString.substr(i, 8);
            result += String.fromCharCode(parseInt(byte, 2));
        }
        return result;
    }

    binaryLetterEncode(str) {
        let binaryString = "";
        for (let i = 0; i < str.length; i++) {
            let charCodeBinary = str.charCodeAt(i).toString(2).padStart(8, '0');
            binaryString += charCodeBinary;
        }
        let result = "";
        for (let i = 0; i < binaryString.length; i += 4) {
            let nibble = binaryString.substr(i, 4);
            if (!this.reverseCharMap[nibble]) {
                throw new Error("BinaryLetter编码失败: 无法转换某些字符");
            }
            result += this.reverseCharMap[nibble];
        }
        return result;
    }

    htmlEncode(str) {
        return str.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#39;');
    }

    htmlDecode(str) {
        str = str.replace(/&amp;/g, '&').replace(/&lt;/g, '<').replace(/&gt;/g, '>').replace(/&quot;/g, '"').replace(/&#39;/g, "'");
        const entityPattern = /&[#0-9a-zA-Z]+;/;
        if (entityPattern.test(str)) {
            throw new Error("HTML解码失败: 字符串包含无法解码的HTML实体");
        }
        return str;
    }

    async showMenu() {
        const topLevelMenu = {
            items: [
                "URL 编码/解码",
                "HTML 编码/解码",
                "Base64 编码/解码",
                "Unicode 编码/解码",
                "BinaryLetter 编码/解码"
            ]
        };
        const { index: topLevelIndex } = await $ui.menu(topLevelMenu.items);
        if (topLevelIndex === undefined) {
            $ui.toast("无效内容/请重新选择", 2);
            return;
        }
        await this.delay(100);
        const subLevelMenus = [
            ["URL 编码", "URL 解码"],
            ["HTML 编码", "HTML 解码"],
            ["Base64 编码", "Base64 解码"],
            ["Unicode 编码", "Unicode 解码"],
            ["BinaryLetter 编码", "BinaryLetter 解码"]
        ];
        const { index: subLevelIndex } = await $ui.menu(subLevelMenus[topLevelIndex]);
        if (subLevelIndex === undefined) {
            $ui.toast("无效内容/请重新选择", 2);
            return;
        }
        await this.delay(100);
        this.handleSelection(topLevelIndex * 2 + subLevelIndex);
    }

    handleSelection(index) {
        try {
            switch (index) {
                case 0:
                    this.text = encodeURIComponent(this.text);
                    break;
                case 1:
                    this.decodeURIComponentWithCheck("URL");
                    break;
                case 2:
                    this.text = this.htmlEncode(this.text);
                    break;
                case 3:
                    this.text = this.htmlDecode(this.text);
                    break;
                case 4:
                    this.text = $text.base64Encode(this.text);
                    break;
                case 5:
                    this.decodeBase64WithCheck();
                    break;
                case 6:
                    this.text = this.text.split('').map(char => '\\u' + char.charCodeAt(0).toString(16).padStart(4, '0')).join('');
                    break;
                case 7:
                    this.decodeUnicodeWithCheck();
                    break;
                case 8:
                    this.text = this.binaryLetterEncode(this.text);
                    break;
                case 9:
                    this.text = this.binaryLetterDecode(this.text);
                    break;
                default:
                    $ui.toast("无效内容/请重新选择", 2);
                    return;
            }
            this.delay(100).then(() => {
                this.copyToClipboard();
            });
        } catch (e) {
            $ui.toast(e.message, 2);
            return;
        }
    }

    decodeURIComponentWithCheck(type) {
        let decodedText = decodeURIComponent(this.text);
        if (decodedText === this.text) {
            throw new Error(`${type}解码失败: 无有效的${type}编码字符串`);
        }
        this.text = decodedText;
    }

    decodeBase64WithCheck() {
        let base64DecodedText = $text.base64Decode(this.text);
        if (!base64DecodedText) {
            throw new Error("Base64解码失败: 无有效的Base64字符串");
        }
        this.text = base64DecodedText;
    }

    decodeUnicodeWithCheck() {
        let decodedText = this.text.replace(/\\u([\dA-F]{4})/gi, (match, grp) => {
            return String.fromCharCode(parseInt(grp, 16));
        });
        if (decodedText === this.text) {
            throw new Error("Unicode解码失败: 无有效的Unicode字符");
        }
        this.text = decodedText;
    }

    copyToClipboard() {
        $clipboard.text = this.text;
        this.showResult();
    }

    showResult() {
        let preview = this.text.length > 40 ? this.text.substring(0, 37) + '...' : this.text;
        $ui.toast(`已复制到剪切板: ${preview}`, 2);
    }

    delay(ms) {
        return new Promise(resolve => setTimeout(resolve, ms));
    }

    requestInput() {
        $input.text({
            placeholder: "剪切板无内容，请输入文本…",
            handler: text => {
                if (text === undefined || text === "") {
                    $ui.toast("未输入任何内容", 2);
                    return;
                }
                this.text = text;
                this.showMenu();
            }
        });
    }

    run() {
        if (this.text !== undefined && this.text !== "") {
            this.showMenu();
        } else {
            this.requestInput();
        }
    }
}

const action = new MyCodecAction();
action.run();
