class MyAction extends Action {
    #component

    get component() {
        if (!this.#component) {
            const url = String(this.text);
            const path = url.substring(url.indexOf("/", "https://".length) + 1);
            this.#component = path.split("/");
        }
        return this.#component;
    }

    l10n() {
        return {
            "zh-Hans": {
                "openLink.nourl": "未检测到链接",
                "linkAlreadyEdited": "链接已转换为ScriptHub编辑状态",
                "linkPreviewEnabled": "链接已转换为CodeHub在线预览",
                "linkPreviewDisabled": "链接已恢复到原始状态"
            },
            en: {
                "openLink.nourl": "No GitHub link detected",
                "linkAlreadyEdited": "Link converted to ScriptHub edit state",
                "linkPreviewEnabled": "Link converted to CodeHub online preview",
                "linkPreviewDisabled": "Link restored to original state"
            }
        }
    }

    processGistRawUrl(url) {
        const regex = /https:\/\/gist\.githubusercontent\.com\/(.+)\/raw\/[0-9a-f]+\/(.+)/;
        const match = url.match(regex);
        if (match && match.length === 3) {
            return `https://gist.githubusercontent.com/${match[1]}/raw/${match[2]}`;
        }
        return url;
    }

    githubusercontent() {
        const user = this.component[0],
            repository = this.component[1],
            branch = this.component[2],
            file = this.component.slice(3).join("/");
        return `https://github.com/${user}/${repository}/blob/${branch}/${file}`;
    }

    github() {
        const user = this.component[0],
            repository = this.component[1],
            blob = this.component[2],
            branch = this.component[3],
            file = this.component.slice(4).join("/");
        return `https://raw.githubusercontent.com/${user}/${repository}/${branch}/${file}`;
    }

    do() {
        let result = String(this.text);

        if (result.startsWith("https://app.linkey.store/EditCode?")) {
            result = result.replace("https://app.linkey.store/EditCode?", "");
            $ui.success($l10n("linkPreviewDisabled"));
        } else {
            if (result.includes("script.hub/edit/")) {
                $ui.warning($l10n("linkAlreadyEdited"));
                return;
            }

            if ((result.endsWith(".snippet") || result.endsWith(".yaml")) && !result.startsWith("https://app.linkey.store/EditCode?")) {
                result = `https://app.linkey.store/EditCode?${result}`;
                $ui.success($l10n("linkPreviewEnabled"));
            } else {
                if (result.includes("script.hub/file/") || result.includes("script.hub/convert/")) {
                    result = result.replace("script.hub/file/", "script.hub/edit/");
                    result = result.replace("script.hub/convert/", "script.hub/edit/");
                    $ui.success($l10n("linkAlreadyEdited"));
                } else if (result.includes("raw.githubusercontent.com")) {
                    result = this.githubusercontent(result);
                } else if (result.includes("github.com")) {
                    if (result.includes("?raw=true")) {
                        result = result.replace("?raw=true", "");
                    } else {
                        result = this.github();
                    }
                } else if (result.includes("gist.githubusercontent.com")) {
                    result = this.processGistRawUrl(result);
                } else {
                    $ui.warning($l10n("openLink.nourl"));
                    return;
                }

                if (!result.startsWith("https://app.linkey.store/EditCode?")) {
                    $ui.success($l10n("COPIED"));
                }
            }
        }

        $clipboard.text = result;
    }
}
